function run(input) {

    let notClientRegExp = new RegExp(/.*(ainda.*n([√Ä-√∫]|[a-z])o)|(n[√£|a]o)|(noa).*/i);
    let clientRegExp = new RegExp(/.*(j(a|√°).*(sou.*cliente)?)|(sim)|(smi)|(sou)|(cliente).*/i);

    if (notClientRegExp.test(input)) {
        return "Nao cliente";
    } else if (clientRegExp.test(input)) {
        return "Cliente";
    }

    return "Looping";
}

/**
* This script is necessary after a flow change where started to have three possibilities to the isCustomer flag.
* Before there was just 2 possibilities: true or false;
* Now: "Cliente", "Nao Cliente", "Looping"
**/
function run(input) {
    if (input === "Cliente" || input === "Nao cliente" || input === "Looping") {
        return input;
    } else if (input || input === "true") {
        return "Cliente";
    } else if (!input || input === "false") {
        return "Nao cliente";
    }
    return input;
}

